CREATE TABLE IF NOT EXISTS hs_kum (
    id INT AUTO_INCREMENT PRIMARY KEY,
    rule_number INT NOT NULL,
    title VARCHAR(255) NOT NULL,
    content TEXT NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    UNIQUE KEY (rule_number)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- Insert KUM HS data
INSERT INTO hs_kum (rule_number, title, content) VALUES (1, 'Aturan 1 - Judul Bagian, Bab dan Sub-bab', 'Judul dari Bagian, Bab dan Sub-bab dimaksudkan hanya untuk mempermudah referensi saja; untuk keperluan hukum, klasifikasi harus ditentukan berdasarkan uraian yang terdapat dalam pos dan berbagai Catatan Bagian atau Bab yang berkaitan serta berdasarkan ketentuan berikut ini, asalkan pos atau Catatan tersebut tidak menentukan lain.') ON DUPLICATE KEY UPDATE title = VALUES(title), content = VALUES(content);
INSERT INTO hs_kum (rule_number, title, content) VALUES (2, 'Aturan 2 - Barang Tidak Lengkap/Belum Selesai', '(a) Setiap referensi pada suatu barang dalam suatu pos harus dianggap meliputi juga referensi pada barang tersebut yang tidak lengkap atau belum selesai, asalkan pada waktu diajukan barang tidak lengkap atau belum selesai itu mempunyai karakter esensial dari barang yang lengkap atau selesai. Pos tersebut juga meliputi barang yang lengkap atau selesai (atau diklasifikasikan sebagai barang lengkap atau selesai berdasarkan Aturan ini), yang diajukan belum dirakit atau copot. (b) Setiap referensi pada suatu bahan atau zat dalam suatu pos harus dianggap meliputi referensi pada campuran atau kombinasi dari bahan atau zat tersebut dengan bahan atau zat lain. Setiap referensi pada barang dari suatu bahan atau zat tertentu harus dianggap meliputi referensi pada barang yang seluruhnya atau sebagian terdiri dari bahan atau zat tersebut. Klasifikasi barang yang terdiri dari lebih dari satu bahan atau zat harus sesuai dengan prinsip-prinsip dalam Aturan 3.') ON DUPLICATE KEY UPDATE title = VALUES(title), content = VALUES(content);
INSERT INTO hs_kum (rule_number, title, content) VALUES (3, 'Aturan 3 - Barang Dapat Diklasifikasi dalam Dua Pos atau Lebih', 'Apabila barang seolah-olah dapat diklasifikasikan dalam dua pos atau lebih, maka klasifikasi harus dilakukan sebagai berikut: (a) Pos yang memberikan uraian paling spesifik harus dipilih daripada pos yang memberikan uraian lebih umum. Namun demikian, apabila dua pos atau lebih masing-masing hanya merujuk pada sebagian dari bahan atau zat yang terkandung dalam barang campuran atau barang komposit, atau hanya pada sebagian dari barang dalam set yang disiapkan untuk penjualan eceran, pos-pos tersebut harus dianggap sama spesifiknya dalam hubungannya dengan barang tersebut, walaupun salah satu pos memberikan uraian yang lebih lengkap atau tepat atas barang tersebut. (b) Barang campuran, barang komposit yang terdiri dari bahan berbeda atau dibuat dari komponen berbeda, dan barang yang disiapkan dalam set untuk penjualan eceran, yang tidak dapat diklasifikasikan dengan mengacu pada Aturan 3(a), harus diklasifikasikan seolah-olah terdiri dari bahan atau komponen yang memberikan karakter esensialnya, sepanjang kriteria ini dapat diterapkan. (c) Apabila barang tidak dapat diklasifikasikan dengan mengacu pada Aturan 3(a) atau 3(b), barang harus diklasifikasikan dalam pos yang muncul terakhir dalam urutan numerik di antara pos-pos yang sama-sama patut dipertimbangkan.') ON DUPLICATE KEY UPDATE title = VALUES(title), content = VALUES(content);
INSERT INTO hs_kum (rule_number, title, content) VALUES (4, 'Aturan 4 - Barang yang Paling Mirip', 'Barang yang tidak dapat diklasifikasikan sesuai dengan Aturan-aturan tersebut di atas, harus diklasifikasikan dalam pos yang sesuai dengan barang yang paling mirip (serupa) dengannya.') ON DUPLICATE KEY UPDATE title = VALUES(title), content = VALUES(content);
INSERT INTO hs_kum (rule_number, title, content) VALUES (5, 'Aturan 5 - Wadah dan Bahan Pengemas', '(a) Kotak kamera, kotak alat musik, kotak senjata, kotak alat gambar, kotak kalung dan wadah serupa, yang dibentuk atau disesuaikan khusus untuk menampung suatu barang atau set tertentu, cocok untuk penggunaan jangka panjang dan diajukan bersama barang yang dimaksudkan untuk ditampung, harus diklasifikasikan dengan barang tersebut apabila jenis ini biasa dijual bersama barang tersebut. Namun demikian, Aturan ini tidak berlaku untuk wadah yang memberikan keseluruhan karakter esensial. (b) Tunduk pada ketentuan Aturan 5(a) di atas, bahan pengemas dan wadah pengemas yang diajukan bersama barang di dalamnya harus diklasifikasikan bersama apabila jenis ini biasa digunakan untuk mengemas barang tersebut. Namun demikian, ketentuan ini tidak mengikat apabila bahan pengemas atau wadah pengemas tersebut jelas cocok untuk penggunaan berulang.') ON DUPLICATE KEY UPDATE title = VALUES(title), content = VALUES(content);
INSERT INTO hs_kum (rule_number, title, content) VALUES (6, 'Aturan 6 - Klasifikasi dalam Subpos', 'Untuk keperluan hukum, klasifikasi barang dalam subpos suatu pos harus ditentukan menurut uraian subpos dan Catatan Subpos yang berkaitan serta mutatis mutandis Aturan di atas, dengan pengertian bahwa hanya subpos pada tingkat yang sama yang dapat diperbandingkan. Untuk keperluan Aturan ini, Catatan Bagian dan Bab yang berkaitan juga berlaku, kecuali apabila konteksnya menentukan lain.') ON DUPLICATE KEY UPDATE title = VALUES(title), content = VALUES(content);

